class CustomInfo {
    String? field;
    String? value;

    CustomInfo({
        required this.field,
        required this.value,
    });

    factory CustomInfo.fromJson(Map<String, dynamic> json) => CustomInfo(
        field: json["field"],
        value: json["value"],
    );

    Map<String, dynamic> toJson() => {
        "field": field,
        "value": value,
    };
}

class Price {
    String? id;
    String? title;
    String? internalCode;
    double? price;
    List<dynamic> barCodes;
    double? qtdStock;

    Price({
        required this.id,
        required this.title,
        required this.internalCode,
        required this.price,
        required this.barCodes,
        required this.qtdStock,
    });

    factory Price.fromJson(Map<String, dynamic> json) => Price(
        id: json["id"],
        title: json["title"],
        internalCode: json["internal_code"],
        price: json["price"]?.toDouble(),
        barCodes: List<dynamic>.from(json["bar_codes"].map((x) => x)),
        qtdStock: json["qtd_stock"]?.toDouble(),
    );

    Map<String, dynamic> toJson() => {
        "id": id,
        "title": title,
        "internal_code": internalCode,
        "price": price,
        "bar_codes": List<dynamic>.from(barCodes.map((x) => x)),
        "qtd_stock": qtdStock,
    };
}

class Shipping {
    bool deliverable;

    Shipping({
        required this.deliverable,
